if event_data['command'] == 'PRIVMSG':
    nick = event_data['prefix'].split('!')[0]
    target = event_data['params_list'][0]
    message = event_data['trailing']
    if target.startswith('#'):
        client.add_message(f"{nick}: {message}", "other_message", context_name=target)
    else:
        client.add_message(f"{nick}: {message}", "pm", context_name=nick)

if event_data['command'] == 'QUIT':
    nick = event_data['prefix'].split('!')[0]
    reason = event_data['trailing'] if event_data['trailing'] else ""
    for ctx_name, ctx in client.context_manager.contexts.items():
        if ctx.type == "channel" and nick in ctx.users:
            client.add_message(f"{nick} quit{' (' + reason + ')' if reason else ''}", "join_part", context_name=ctx_name)
